import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getApiUrl, getAuthHeaders } from '../../config';
import { useAuth } from '../../context/AuthContext';
import { useToast } from '../../context/ToastContext';
import { FiArrowLeft, FiUser, FiBook, FiMail, FiUsers } from 'react-icons/fi';
import {
  validateUsername,
  validatePassword,
  validateEmail,
  validatePhone,
  validateRequired,
  validateDate,
  validateTextLength,
  validateFullName,
  validateSchoolCode,
  validateStudentId,
} from '../../utils/validation';

export default function StudentCreate() {
  const navigate = useNavigate();
  const { token } = useAuth();
  const toast = useToast();
  const [form, setForm] = useState({
    username: '',
    password: '',
    fullName: '',
    gender: '',
    dateOfBirth: '',
    bloodGroup: '',
    nationality: '',
    motherTongue: '',
    languagePreference: '',
    gradeLevel: '',
    section: '',
    academicYear: '',
    admissionDate: '',
    status: 'Active',
    admissionNumber: '',
    rollNo: '',
    phoneNumber: '',
    schoolEmail: '',
    personalEmail: '',
    guardianName: '',
    guardianRelation: '',
    guardianContact: '',
    schoolCode: '',
  });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});

  const handleFieldChange = (field, value) => {
    setForm({ ...form, [field]: value });
    if (fieldErrors[field]) {
      setFieldErrors({ ...fieldErrors, [field]: '' });
    }
  };

  const validateForm = () => {
    const errors = {};
    let isValid = true;

    // Account section
    const usernameError = validateUsername(form.username);
    if (usernameError) {
      errors.username = usernameError;
      isValid = false;
    }

    const passwordError = validatePassword(form.password);
    if (passwordError) {
      errors.password = passwordError;
      isValid = false;
    }

    // Personal Info section
    const fullNameError = validateFullName(form.fullName);
    if (fullNameError) {
      errors.fullName = fullNameError;
      isValid = false;
    }

    const genderError = validateRequired(form.gender, 'Gender');
    if (genderError) {
      errors.gender = genderError;
      isValid = false;
    }

    const dobError = validateDate(form.dateOfBirth, 'Date of birth');
    if (dobError) {
      errors.dateOfBirth = dobError;
      isValid = false;
    }

    if (form.bloodGroup) {
      const bloodGroupError = validateTextLength(form.bloodGroup, 'Blood group', 0, 10);
      if (bloodGroupError) {
        errors.bloodGroup = bloodGroupError;
        isValid = false;
      }
    }

    if (form.nationality) {
      const nationalityError = validateTextLength(form.nationality, 'Nationality', 0, 50);
      if (nationalityError) {
        errors.nationality = nationalityError;
        isValid = false;
      }
    }

    if (form.motherTongue) {
      const motherTongueError = validateTextLength(form.motherTongue, 'Mother tongue', 0, 50);
      if (motherTongueError) {
        errors.motherTongue = motherTongueError;
        isValid = false;
      }
    }

    if (form.languagePreference) {
      const langError = validateTextLength(form.languagePreference, 'Language preference', 0, 50);
      if (langError) {
        errors.languagePreference = langError;
        isValid = false;
      }
    }

    // Academic Info section
    const schoolCodeError = validateSchoolCode(form.schoolCode);
    if (schoolCodeError) {
      errors.schoolCode = schoolCodeError;
      isValid = false;
    }

    const gradeLevelError = validateRequired(form.gradeLevel, 'Class');
    if (gradeLevelError) {
      errors.gradeLevel = gradeLevelError;
      isValid = false;
    }

    const sectionError = validateRequired(form.section, 'Section');
    if (sectionError) {
      errors.section = sectionError;
      isValid = false;
    }

    const academicYearError = validateRequired(form.academicYear, 'Academic year');
    if (academicYearError) {
      errors.academicYear = academicYearError;
      isValid = false;
    }

    const admissionDateError = validateDate(form.admissionDate, 'Admission date', true);
    if (admissionDateError) {
      errors.admissionDate = admissionDateError;
      isValid = false;
    }

    const admissionNumberError = validateStudentId(form.admissionNumber, 'Admission number');
    if (admissionNumberError) {
      errors.admissionNumber = admissionNumberError;
      isValid = false;
    }

    const rollNoError = validateStudentId(form.rollNo, 'Roll number');
    if (rollNoError) {
      errors.rollNo = rollNoError;
      isValid = false;
    }

    // Contact Details section
    if (form.phoneNumber) {
      const phoneError = validatePhone(form.phoneNumber);
      if (phoneError) {
        errors.phoneNumber = phoneError;
        isValid = false;
      }
    }

    const schoolEmailError = validateEmail(form.schoolEmail);
    if (schoolEmailError) {
      errors.schoolEmail = schoolEmailError;
      isValid = false;
    } else if (!form.schoolEmail || form.schoolEmail.trim() === '') {
      errors.schoolEmail = 'School email is required';
      isValid = false;
    }

    if (form.personalEmail) {
      const personalEmailError = validateEmail(form.personalEmail);
      if (personalEmailError) {
        errors.personalEmail = personalEmailError;
        isValid = false;
      }
    }

    // Guardian Info section
    if (form.guardianName) {
      const guardianNameError = validateTextLength(form.guardianName, 'Guardian name', 2, 100);
      if (guardianNameError) {
        errors.guardianName = guardianNameError;
        isValid = false;
      }
    }

    if (form.guardianRelation) {
      const guardianRelationError = validateTextLength(form.guardianRelation, 'Guardian relation', 0, 50);
      if (guardianRelationError) {
        errors.guardianRelation = guardianRelationError;
        isValid = false;
      }
    }

    if (form.guardianContact) {
      const guardianContactError = validatePhone(form.guardianContact);
      if (guardianContactError) {
        errors.guardianContact = guardianContactError;
        isValid = false;
      }
    }

    setFieldErrors(errors);
    return isValid;
  };

  return (
    <div className="page">
      <div className="page-header">
        <h2>Create New Student</h2>
        <button className="btn-secondary" onClick={() => navigate('/students')}>
          <FiArrowLeft size={16} style={{ marginRight: 8 }} />
          Back to List
        </button>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      <form
        onSubmit={async (e) => {
          e.preventDefault();
          setError('');
          setSubmitting(true);
          try {
            const payload = {
              username: String(form.username || '').trim(),
              password: String(form.password || '').trim(),
              fullName: String(form.fullName || '').trim(),
              gender: String(form.gender || '').trim(),
              dateOfBirth: String(form.dateOfBirth || '').trim(),
              bloodGroup: String(form.bloodGroup || '').trim(),
              nationality: String(form.nationality || '').trim(),
              motherTongue: String(form.motherTongue || '').trim(),
              languagePreference: String(form.languagePreference || '').trim(),
              gradeLevel: String(form.gradeLevel || '').trim(),
              section: String(form.section || '').trim(),
              academicYear: String(form.academicYear || '').trim(),
              admissionDate: String(form.admissionDate || '').trim(),
              status: String(form.status || 'Active').trim(),
              admissionNumber: String(form.admissionNumber || '').trim(),
              rollNo: String(form.rollNo || '').trim(),
              phoneNumber: String(form.phoneNumber || '').trim(),
              schoolEmail: String(form.schoolEmail || '').trim(),
              personalEmail: String(form.personalEmail || '').trim(),
              guardianName: String(form.guardianName || '').trim(),
              guardianRelation: String(form.guardianRelation || '').trim(),
              guardianContact: String(form.guardianContact || '').trim(),
              schoolCode: String(form.schoolCode || '').trim(),
            };
            const url = getApiUrl('/midland/admin/students/create');
            const res = await fetch(url, {
              method: 'POST',
              headers: getAuthHeaders(token),
              body: JSON.stringify(payload),
            });

            if (!res.ok) {
              let errorMessage = `Failed to create student (${res.status})`;
              try {
                const errorData = await res.json();
                // Extract message from backend error response
                if (errorData.message) {
                  errorMessage = errorData.message;
                  // If it's a username conflict, highlight the username field
                  if (errorMessage.toLowerCase().includes('username') && errorMessage.toLowerCase().includes('already exists')) {
                    setFieldErrors({ ...fieldErrors, username: errorMessage });
                  }
                } else if (errorData.error) {
                  errorMessage = errorData.error;
                }
              } catch {
                // If JSON parsing fails, try text
                const errorText = await res.text().catch(() => '');
                if (errorText) {
                  try {
                    const parsed = JSON.parse(errorText);
                    errorMessage = parsed.message || parsed.error || errorText;
                  } catch {
                    errorMessage = errorText || errorMessage;
                  }
                }
              }
              throw new Error(errorMessage);
            }

            toast.success('Student created successfully');
            navigate('/students', { replace: true });
          } catch (err) {
            const errorMsg = err.message || 'Failed to create student';
            setError(errorMsg);
            toast.error(errorMsg);
          } finally {
            setSubmitting(false);
          }
        }}
      >
        <div className="form-section">
          <h3>
            <FiUser />
            Account
          </h3>
          <div className="form-grid">
            <label>
              Username *
              <input 
                value={form.username} 
                onChange={(e) => handleFieldChange('username', e.target.value)} 
                className={fieldErrors.username ? 'input-error' : ''}
                required 
              />
              {fieldErrors.username && <span className="field-error">{fieldErrors.username}</span>}
            </label>
            <label>
              Password *
              <input type="password" value={form.password} onChange={(e) => handleFieldChange('password', e.target.value)} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiUser />
            Personal Info
          </h3>
          <div className="form-grid">
            <label>
              Full Name *
              <input value={form.fullName} onChange={(e) => handleFieldChange('fullName', e.target.value)} required />
            </label>
            <label>
              Gender *
              <select value={form.gender} onChange={(e) => setForm({ ...form, gender: e.target.value })} required>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </label>
            <label>
              Date of Birth *
              <input type="date" value={form.dateOfBirth} onChange={(e) => setForm({ ...form, dateOfBirth: e.target.value })} required />
            </label>
            <label>
              Blood Group
              <input value={form.bloodGroup} onChange={(e) => setForm({ ...form, bloodGroup: e.target.value })} />
            </label>
            <label>
              Nationality
              <input value={form.nationality} onChange={(e) => handleFieldChange('nationality', e.target.value)} />
            </label>
            <label>
              Mother Tongue
              <input value={form.motherTongue} onChange={(e) => handleFieldChange('motherTongue', e.target.value)} />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiBook />
            Academic Info
          </h3>
          <div className="form-grid">
            <label>
              School Code *
              <input value={form.schoolCode} onChange={(e) => setForm({ ...form, schoolCode: e.target.value })} required />
            </label>
            <label>
              Class *
              <input value={form.gradeLevel} onChange={(e) => handleFieldChange('gradeLevel', e.target.value)} required />
            </label>
            <label>
              Section *
              <input value={form.section} onChange={(e) => handleFieldChange('section', e.target.value)} required />
            </label>
            <label>
              Academic Year *
              <input value={form.academicYear} onChange={(e) => handleFieldChange('academicYear', e.target.value)} required />
            </label>
            <label>
              Admission Date *
              <input type="date" value={form.admissionDate} onChange={(e) => setForm({ ...form, admissionDate: e.target.value })} required />
            </label>
            <label>
              Status
              <select value={form.status} onChange={(e) => setForm({ ...form, status: e.target.value })}>
                <option value="Active">Active</option>
                <option value="Inactive">Inactive</option>
              </select>
            </label>
            <label>
              Admission No *
              <input value={form.admissionNumber} onChange={(e) => handleFieldChange('admissionNumber', e.target.value)} required />
            </label>
            <label>
              Roll No *
              <input value={form.rollNo} onChange={(e) => handleFieldChange('rollNo', e.target.value)} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiMail />
            Contact Details
          </h3>
          <div className="form-grid">
            <label>
              Phone Number
              <input value={form.phoneNumber} onChange={(e) => handleFieldChange('phoneNumber', e.target.value)} />
            </label>
            <label>
              School Email *
              <input type="email" value={form.schoolEmail} onChange={(e) => handleFieldChange('schoolEmail', e.target.value)} required />
            </label>
            <label>
              Personal Email
              <input type="email" value={form.personalEmail} onChange={(e) => handleFieldChange('personalEmail', e.target.value)} />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiUsers />
            Guardian Info
          </h3>
          <div className="form-grid">
            <label>
              Guardian Name
              <input value={form.guardianName} onChange={(e) => handleFieldChange('guardianName', e.target.value)} />
            </label>
            <label>
              Guardian Relation
              <input value={form.guardianRelation} onChange={(e) => handleFieldChange('guardianRelation', e.target.value)} />
            </label>
            <label>
              Guardian Contact
              <input value={form.guardianContact} onChange={(e) => handleFieldChange('guardianContact', e.target.value)} />
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn-secondary" onClick={() => navigate('/students')}>
            Cancel
          </button>
          <button type="submit" className="btn-primary" disabled={submitting}>
            {submitting ? 'Creating...' : 'Create Student'}
          </button>
        </div>
      </form>
    </div>
  );
}
