import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getApiUrl, getAuthHeaders } from '../../config';
import { useAuth } from '../../context/AuthContext';
import { useToast } from '../../context/ToastContext';
import { FiArrowLeft, FiUser, FiBriefcase, FiMail } from 'react-icons/fi';
import {
  validateUsername,
  validatePassword,
  validateEmail,
  validatePhone,
  validateRequired,
  validateDate,
  validateTextLength,
  validateFullName,
  validateSchoolCode,
  validateTeacherCode,
} from '../../utils/validation';

export default function TeacherCreate() {
  const navigate = useNavigate();
  const { token } = useAuth();
  const toast = useToast();
  const [form, setForm] = useState({
    username: '',
    password: '',
    fullName: '',
    gender: '',
    dateOfBirth: '',
    department: '',
    designation: '',
    teacherCode: '',
    schoolEmail: '',
    personalEmail: '',
    phoneNumber: '',
    schoolCode: '',
  });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});

  return (
    <div className="page">
      <div className="page-header">
        <h2>Create New Teacher</h2>
        <button className="btn-secondary" onClick={() => navigate('/teachers')}>
          <FiArrowLeft size={16} style={{ marginRight: 8 }} />
          Back to List
        </button>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      <form
        onSubmit={async (e) => {
          e.preventDefault();
          setError('');
          setSubmitting(true);
          try {
            const payload = {
              username: String(form.username || '').trim(),
              password: String(form.password || '').trim(),
              fullName: String(form.fullName || '').trim(),
              gender: String(form.gender || '').trim(),
              dateOfBirth: String(form.dateOfBirth || '').trim(),
              department: String(form.department || '').trim(),
              designation: String(form.designation || '').trim(),
              teacherCode: String(form.teacherCode || '').trim(),
              schoolEmail: String(form.schoolEmail || '').trim(),
              personalEmail: String(form.personalEmail || '').trim(),
              phoneNumber: String(form.phoneNumber || '').trim(),
              schoolCode: String(form.schoolCode || '').trim(),
            };
            const url = getApiUrl('/midland/admin/teachers/create');
            const res = await fetch(url, {
              method: 'POST',
              headers: getAuthHeaders(token),
              body: JSON.stringify(payload),
            });

            if (!res.ok) {
              let errorMessage = `Failed to create teacher (${res.status})`;
              try {
                const errorData = await res.json();
                // Extract message from backend error response
                if (errorData.message) {
                  errorMessage = errorData.message;
                  // If it's a username conflict, highlight the username field
                  if (errorMessage.toLowerCase().includes('username') && errorMessage.toLowerCase().includes('already exists')) {
                    setFieldErrors({ ...fieldErrors, username: errorMessage });
                  }
                } else if (errorData.error) {
                  errorMessage = errorData.error;
                }
              } catch {
                // If JSON parsing fails, try text
                const errorText = await res.text().catch(() => '');
                if (errorText) {
                  try {
                    const parsed = JSON.parse(errorText);
                    errorMessage = parsed.message || parsed.error || errorText;
                  } catch {
                    errorMessage = errorText || errorMessage;
                  }
                }
              }
              throw new Error(errorMessage);
            }

            toast.success('Teacher created successfully');
            navigate('/teachers', { replace: true });
          } catch (err) {
            const errorMsg = err.message || 'Failed to create teacher';
            setError(errorMsg);
            toast.error(errorMsg);
          } finally {
            setSubmitting(false);
          }
        }}
      >
        <div className="form-section">
          <h3>
            <FiUser />
            Account
          </h3>
          <div className="form-grid">
            <label>
              Username *
              <input 
                value={form.username} 
                onChange={(e) => {
                  setForm({ ...form, username: e.target.value });
                  if (fieldErrors.username) {
                    setFieldErrors({ ...fieldErrors, username: '' });
                  }
                }}
                className={fieldErrors.username ? 'input-error' : ''}
                required 
              />
              {fieldErrors.username && <span className="field-error">{fieldErrors.username}</span>}
            </label>
            <label>
              Password *
              <input type="password" value={form.password} onChange={(e) => setForm({ ...form, password: e.target.value })} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiUser />
            Personal Info
          </h3>
          <div className="form-grid">
            <label>
              Full Name *
              <input value={form.fullName} onChange={(e) => setForm({ ...form, fullName: e.target.value })} required />
            </label>
            <label>
              Gender *
              <select value={form.gender} onChange={(e) => setForm({ ...form, gender: e.target.value })} required>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </label>
            <label>
              Date of Birth *
              <input type="date" value={form.dateOfBirth} onChange={(e) => setForm({ ...form, dateOfBirth: e.target.value })} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiBriefcase />
            Professional Info
          </h3>
          <div className="form-grid">
            <label>
              Department *
              <input value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })} required />
            </label>
            <label>
              Designation *
              <input value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })} required />
            </label>
            <label>
              Teacher Code *
              <input value={form.teacherCode} onChange={(e) => setForm({ ...form, teacherCode: e.target.value })} required />
            </label>
            <label>
              School Code *
              <input value={form.schoolCode} onChange={(e) => setForm({ ...form, schoolCode: e.target.value })} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiMail />
            Contact Details
          </h3>
          <div className="form-grid">
            <label>
              Phone Number
              <input value={form.phoneNumber} onChange={(e) => setForm({ ...form, phoneNumber: e.target.value })} />
            </label>
            <label>
              School Email *
              <input type="email" value={form.schoolEmail} onChange={(e) => setForm({ ...form, schoolEmail: e.target.value })} required />
            </label>
            <label>
              Personal Email
              <input type="email" value={form.personalEmail} onChange={(e) => setForm({ ...form, personalEmail: e.target.value })} />
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn-secondary" onClick={() => navigate('/teachers')}>
            Cancel
          </button>
          <button type="submit" className="btn-primary" disabled={submitting}>
            {submitting ? 'Creating...' : 'Create Teacher'}
          </button>
        </div>
      </form>
    </div>
  );
}
