import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getApiUrl, getAuthHeaders } from '../../config';
import { useAuth } from '../../context/AuthContext';
import { useToast } from '../../context/ToastContext';
import { FiArrowLeft, FiUser, FiLock, FiBriefcase, FiMail } from 'react-icons/fi';

export default function AdminCreate() {
  const navigate = useNavigate();
  const { token } = useAuth();
  const toast = useToast();
  const [form, setForm] = useState({
    username: '',
    password: '',
    fullName: '',
    gender: '',
    designation: '',
    schoolEmail: '',
    personalEmail: '',
    phoneNumber: '',
    schoolCode: '',
  });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  return (
    <div className="page">
      <div className="page-header">
        <h2>Create Administrator</h2>
        <button className="btn-secondary" onClick={() => navigate('/admin')}>
          <FiArrowLeft size={16} style={{ marginRight: 8 }} />
          Back to List
        </button>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      <form
        onSubmit={async (e) => {
          e.preventDefault();
          setError('');
          setSubmitting(true);
          try {
            const payload = {
              username: String(form.username || '').trim(),
              password: String(form.password || '').trim(),
              fullName: String(form.fullName || '').trim(),
              gender: String(form.gender || '').trim(),
              designation: String(form.designation || '').trim(),
              schoolEmail: String(form.schoolEmail || '').trim(),
              personalEmail: String(form.personalEmail || '').trim(),
              phoneNumber: String(form.phoneNumber || '').trim(),
              schoolCode: String(form.schoolCode || '').trim(),
            };
            const url = getApiUrl('/midland/admin/create');
            const res = await fetch(url, {
              method: 'POST',
              headers: getAuthHeaders(token),
              body: JSON.stringify(payload),
            });

            if (!res.ok) {
              const errorText = await res.text().catch(() => '');
              throw new Error(errorText || `Failed to create: ${res.status}`);
            }

            toast.success('Administrator created successfully');
            navigate('/admin', { replace: true });
          } catch (err) {
            const errorMsg = err.message || 'Failed to create admin';
            setError(errorMsg);
            toast.error(errorMsg);
          } finally {
            setSubmitting(false);
          }
        }}
      >
        <div className="form-section">
          <h3>
            <FiUser size={18} style={{ marginRight: 8, verticalAlign: 'middle' }} />
            Account
          </h3>
          <div className="form-grid">
            <label>
              Username *
              <input value={form.username} onChange={(e) => setForm({ ...form, username: e.target.value })} required />
            </label>
            <label>
              Password *
              <input type="password" value={form.password} onChange={(e) => setForm({ ...form, password: e.target.value })} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiUser size={18} style={{ marginRight: 8, verticalAlign: 'middle' }} />
            Personal Info
          </h3>
          <div className="form-grid">
            <label>
              Full Name *
              <input value={form.fullName} onChange={(e) => setForm({ ...form, fullName: e.target.value })} required />
            </label>
            <label>
              Gender *
              <select value={form.gender} onChange={(e) => setForm({ ...form, gender: e.target.value })} required>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </label>
            <label>
              Designation *
              <input value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })} required />
            </label>
            <label>
              School Code *
              <input value={form.schoolCode} onChange={(e) => setForm({ ...form, schoolCode: e.target.value })} required />
            </label>
          </div>
        </div>

        <div className="form-section">
          <h3>
            <FiMail size={18} style={{ marginRight: 8, verticalAlign: 'middle' }} />
            Contact Details
          </h3>
          <div className="form-grid">
            <label>
              Phone Number
              <input value={form.phoneNumber} onChange={(e) => setForm({ ...form, phoneNumber: e.target.value })} />
            </label>
            <label>
              School Email *
              <input type="email" value={form.schoolEmail} onChange={(e) => setForm({ ...form, schoolEmail: e.target.value })} required />
            </label>
            <label>
              Personal Email
              <input type="email" value={form.personalEmail} onChange={(e) => setForm({ ...form, personalEmail: e.target.value })} />
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn-secondary" onClick={() => navigate('/admin')}>
            Cancel
          </button>
          <button type="submit" className="btn-primary" disabled={submitting}>
            {submitting ? 'Creating...' : 'Create Administrator'}
          </button>
        </div>
      </form>
    </div>
  );
}
